import React, { useState, useEffect, useCallback } from 'react';
import Step from './components/Step';
import Think from './components/Think';
import Text from './components/Text';
import sseService from './services/sseService';
import './App.css';

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [messageCount, setMessageCount] = useState(0);
  const [lastUpdate, setLastUpdate] = useState('-');
  const [speed, setSpeed] = useState(1000);
  const [components, setComponents] = useState([]);
  const [isStreaming, setIsStreaming] = useState(false);

  // 处理SSE消息
  const handleSSEMessage = useCallback((eventType, data) => {
    switch (eventType) {
      case 'connected':
        setIsConnected(true);
        break;
      case 'disconnected':
        setIsConnected(false);
        break;
      case 'error':
        console.error('SSE错误:', data);
        break;
      case 'message':
        handleNewMessage(data);
        break;
      case 'reset':
        setComponents([]);
        setMessageCount(0);
        break;
      case 'complete':
        setIsStreaming(false);
        break;
      default:
        break;
    }
    setLastUpdate(new Date().toLocaleTimeString());
  }, []);

  // 处理新消息
  const handleNewMessage = useCallback((data) => {
    setMessageCount(prev => prev + 1);

    if (data.type === 'step') {
      setComponents(prev => {
        const newComponents = [...prev];
        const existingStepIndex = newComponents.findIndex(comp => comp.type === 'step');

        if (existingStepIndex >= 0) {
          // 更新现有步骤
          newComponents[existingStepIndex] = {
            ...newComponents[existingStepIndex],
            data: data,
            isFinished: data.content?.is_finished || false
          };
        } else {
          // 添加新步骤
          newComponents.push({
            id: data.msg_id,
            type: 'step',
            data: data,
            isFinished: data.content?.is_finished || false
          });
        }
        return newComponents;
      });
    } else if (data.type === 'think') {
      setComponents(prev => {
        const newComponents = [...prev];
        const existingThinkIndex = newComponents.findIndex(comp => comp.type === 'think');

        if (existingThinkIndex >= 0) {
          // 更新现有思考
          newComponents[existingThinkIndex] = {
            ...newComponents[existingThinkIndex],
            data: data,
            isFinished: data.content?.is_finished || false
          };
        } else {
          // 添加新思考
          newComponents.push({
            id: data.msg_id,
            type: 'think',
            data: data,
            isFinished: data.content?.is_finished || false
          });
        }
        return newComponents;
      });
    } else if (data.type === 'text') {
      setComponents(prev => {
        const newComponents = [...prev];
        const existingTextIndex = newComponents.findIndex(comp => comp.type === 'text');

        if (existingTextIndex >= 0) {
          // 更新现有文本
          newComponents[existingTextIndex] = {
            ...newComponents[existingTextIndex],
            data: data,
            isFinished: data.content?.is_finished || false
          };
        } else {
          // 添加新文本
          newComponents.push({
            id: data.msg_id,
            type: 'text',
            data: data,
            isFinished: data.content?.is_finished || false
          });
        }
        return newComponents;
      });
    }
  }, []);

  // 连接SSE
  const connect = useCallback(() => {
    sseService.connect();
  }, []);

  // 断开连接
  const disconnect = useCallback(() => {
    sseService.disconnect();
  }, []);

  // 开始推送
  const startStream = useCallback(async () => {
    if (!isConnected) {
      alert('请先连接SSE');
      return;
    }

    try {
      await sseService.startStream(speed);
      setIsStreaming(true);
    } catch (error) {
      console.error('开始推送失败:', error);
      alert('开始推送失败: ' + error.message);
    }
  }, [isConnected, speed]);

  // 停止推送
  const stopStream = useCallback(async () => {
    try {
      await sseService.stopStream();
      setIsStreaming(false);
    } catch (error) {
      console.error('停止推送失败:', error);
    }
  }, []);

  // 重置
  const reset = useCallback(async () => {
    try {
      await sseService.reset();
    } catch (error) {
      console.error('重置失败:', error);
    }
  }, []);

  // 清空消息
  const clearMessages = useCallback(() => {
    setComponents([]);
    setMessageCount(0);
  }, []);

  // 组件完成回调
  const handleComponentComplete = useCallback((componentId) => {
    console.log(`组件 ${componentId} 完成`);
  }, []);

  // 注册SSE监听器
  useEffect(() => {
    const unsubscribe = sseService.addListener(handleSSEMessage);
    return unsubscribe;
  }, [handleSSEMessage]);

  // 渲染组件
  const renderComponent = (component) => {
    const commonProps = {
      key: component.id,
      data: component.data,
      isFinished: component.isFinished,
      onComplete: () => handleComponentComplete(component.id)
    };

    switch (component.type) {
      case 'step':
        return <Step {...commonProps} />;
      case 'think':
        return <Think {...commonProps} />;
      case 'text':
        return <Text {...commonProps} />;
      default:
        return null;
    }
  };

  return (
    <div className="App">
      <div className="container">


        <div className={`status ${isConnected ? 'status-connected' : 'status-disconnected'}`}>
          {isConnected ? '已连接' : '未连接'}
        </div>

        <div className="stats">
          <div className="stat-item">
            <strong>接收消息:</strong> {messageCount}
          </div>
          <div className="stat-item">
            <strong>连接状态:</strong> {isConnected ? '已连接' : '断开'}
          </div>
          <div className="stat-item">
            <strong>最后更新:</strong> {lastUpdate}
          </div>
          <div className="stat-item">
            <strong>推送状态:</strong> {isStreaming ? '进行中' : '已停止'}
          </div>
        </div>

        <div className="controls">
          <button 
            className="btn btn-primary" 
            onClick={connect}
            disabled={isConnected}
          >
            连接SSE
          </button>
          <button 
            className="btn btn-success" 
            onClick={startStream}
            disabled={!isConnected || isStreaming}
          >
            开始推送
          </button>
          <button 
            className="btn btn-warning" 
            onClick={stopStream}
            disabled={!isStreaming}
          >
            停止推送
          </button>
          <button 
            className="btn btn-danger" 
            onClick={disconnect}
            disabled={!isConnected}
          >
            断开连接
          </button>
          <button 
            className="btn btn-primary" 
            onClick={clearMessages}
          >
            清空消息
          </button>
          <button 
            className="btn btn-warning" 
            onClick={reset}
          >
            重置数据
          </button>

          <div className="speed-control">
            <label>推送速度:</label>
            <select 
              value={speed} 
              onChange={(e) => setSpeed(parseInt(e.target.value))}
              disabled={isStreaming}
            >
              <option value={500}>快速 (500ms)</option>
              <option value={1000}>正常 (1s)</option>
              <option value={2000}>慢速 (2s)</option>
              <option value={3000}>很慢 (3s)</option>
            </select>
          </div>
        </div>

        <div className="content-area">
          {components.length === 0 ? (
            <div className="empty-state">
              <p>等待数据...</p>
              <p>请先连接SSE，然后开始推送数据</p>
            </div>
          ) : (
            <div className="components-container">
              {components.map(renderComponent)}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
