import React, { useState, useEffect } from 'react';
import './Reply.css';

const Reply = ({ data, isFinished, onComplete }) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    if (data && data.content && data.msg_id) {
      const content = data.content.content || '';

      // Áõ¥Êé•Â§ÑÁêÜÂÜÖÂÆπÔºå‰∏çËøõË°åÂéªÈáçÊ£ÄÊü•
      if (content && !isTyping) {
        typeText(content);
      }
    }
  }, [data, isTyping]);

  useEffect(() => {
    if (isFinished && data?.content?.is_finished) {
      setIsTyping(false);
      if (onComplete) {
        onComplete();
      }
    }
  }, [isFinished, data, onComplete]);

  const typeText = (text) => {
    setIsTyping(true);
    setDisplayText(prev => prev + text); // ËøΩÂä†ÊñáÊú¨ËÄå‰∏çÊòØÊõøÊç¢

    // Ê®°ÊãüÂõûÂ§çÂª∂Ëøü
    setTimeout(() => {
      setIsTyping(false);
    }, 120);
  };

  const getTitle = () => {
    return data?.content?.title || 'AIÂõûÂ§ç';
  };

  return (
    <div className={`reply-container ${isFinished ? 'finished' : ''}`}>
      <div className="reply-header">
        <div className="reply-icon">
          <span className="reply-symbol">üí¨</span>
        </div>
        <div className="reply-title">{getTitle()}</div>
      </div>
      <div className="reply-content">
        {displayText && (
          <div className="reply-text">
            {displayText}
            {isTyping && <span className="cursor">|</span>}
          </div>
        )}
      </div>
    </div>
  );
};

export default Reply;
