import React, { useState, useEffect } from 'react';
import './Think.css';
import Search from './Search';

const Think = ({ data, isFinished, onComplete }) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showComplete, setShowComplete] = useState(false);
  const [currentTitle, setCurrentTitle] = useState('');

  useEffect(() => {
    if (data && data.content) {
      const content = data.content.content || '';
      const title = data.content.title || '';

      if (title && title !== currentTitle) {
        setCurrentTitle(title);
      }

      if (content && !isTyping) {
        typeText(content);
      }
    }
  }, [data]);

  useEffect(() => {
    if (isFinished && data?.content?.is_finished) {
      setShowComplete(true);
      setIsTyping(false);
      if (onComplete) {
        onComplete();
      }
    }
  }, [isFinished, data, onComplete]);

  const typeText = (text) => {
    setIsTyping(true);
    setDisplayText(prev => prev + text); // ËøΩÂä†ÊñáÊú¨ËÄå‰∏çÊòØÊõøÊç¢

    // Ê®°ÊãüÊÄùËÄÉÂª∂Ëøü
    setTimeout(() => {
      setIsTyping(false);
    }, 100);
  };


  return (
    <div className={`think-container ${isFinished ? 'finished' : ''}`}>
      <div className="think-header">
        <div className="think-icon">
          <span className="brain-icon">üß†</span>
        </div>
        <div className="think-title">{getTitle()}</div>
      </div>
      <div className="think-content">
        {/* Â¶ÇÊûúÊòØsearchÁ±ªÂûãÔºåÊòæÁ§∫SearchÁªÑ‰ª∂ */}
        {data?.content?.type === 'search' && (
          <div className="think-search-section">
            <Search data={data.content} isFinished={isFinished} />
          </div>
        )}

        {/* ÊòæÁ§∫ÊÄùËÄÉÊñáÂ≠óÂÜÖÂÆπ */}
        <div className="think-text">
          {displayText}
          {isTyping && <span className="cursor">|</span>}
        </div>
      </div>
    </div>
  );
};

export default Think;
