import React, { useState, useEffect } from 'react';
import './Step.css';

const Step = ({ data, isFinished, onComplete }) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showCheckmark, setShowCheckmark] = useState(false);

  useEffect(() => {
    if (data && data.content) {
      const text = data.content.text || '';
      if (text && !isTyping) {
        typeText(text);
      }
    }
  }, [data]);

  useEffect(() => {
    if (isFinished && data?.content?.is_finished) {
      setShowCheckmark(true);
      if (onComplete) {
        onComplete();
      }
    }
  }, [isFinished, data, onComplete]);

  const typeText = (text) => {
    setIsTyping(true);
    setDisplayText(prev => prev + text); // 追加文本而不是替换

    // 模拟步骤延迟
    setTimeout(() => {
      setIsTyping(false);
    }, 100);
  };

  return (
    <div className={`step-container ${isFinished ? 'finished' : ''}`}>
      <div className="step-header">
        <div className="step-icon">
          {showCheckmark ? (
            <span className="checkmark">✓</span>
          ) : (
            <span className="step-number">1</span>
          )}
        </div>
        <div className="step-title">步骤</div>
        {isTyping && <div className="typing-indicator">正在输入...</div>}
      </div>
      <div className="step-content">
        <div className="step-text">
          {displayText}
          {isTyping && <span className="cursor">|</span>}
        </div>
      </div>
    </div>
  );
};

export default Step;
