import React, { useState, useEffect } from 'react';
import './Text.css';

const Text = ({ data, isFinished, onComplete }) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [showComplete, setShowComplete] = useState(false);
  const [lastMsgId, setLastMsgId] = useState('');

  useEffect(() => {
    if (data && data.content) {
      const content = data.content.content || '';
      const msgId = data.msg_id;

      // 如果是新的消息ID，说明是新的数据流，重置显示文本
      if (msgId !== lastMsgId) {
        setDisplayText('');
        setLastMsgId(msgId);
      }

      if (content && !isTyping) {
        typeText(content);
      }
    }
  }, [data, isTyping, lastMsgId]);

  useEffect(() => {
    if (isFinished && data?.content?.is_finished) {
      setShowComplete(true);
      setIsTyping(false);
      if (onComplete) {
        onComplete();
      }
    }
  }, [isFinished, data, onComplete]);

  const typeText = (text) => {
    setIsTyping(true);
    setDisplayText(prev => prev + text); // 追加文本而不是替换

    // 模拟打字延迟
    setTimeout(() => {
      setIsTyping(false);
    }, 80);
  };

  return (
    <div className={`text-container ${isFinished ? 'finished' : ''}`}>
      <div className="text-header">
        <div className="text-title">文本输出</div>
      </div>
      <div className="text-content">
        <div className="text-output">
          {displayText}
          {isTyping && <span className="cursor">|</span>}
        </div>
      </div>
    </div>
  );
};

export default Text;
