import React, { useState, useEffect, useRef } from 'react';
import './Text.css';

const Text = ({ data, isFinished, onComplete }) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const lastProcessedMsgIdRef = useRef('');

  // 打字机效果相关状态
  const [textQueue, setTextQueue] = useState(''); // 待显示的文本队列
  const typewriterTimerRef = useRef(null); // 打字机定时器

  useEffect(() => {
    if (data && data.content && data.msg_id) {
      const content = data.content.content || '';

      // 只有当消息ID不同时才处理，避免重复处理同一消息
      if (content && lastProcessedMsgIdRef.current !== data.msg_id) {
        lastProcessedMsgIdRef.current = data.msg_id;
        addTextToQueue(content);
      }
    }
  }, [data]);

  useEffect(() => {
    if (isFinished && data?.content?.is_finished) {
      // 清理打字机定时器
      if (typewriterTimerRef.current) {
        clearInterval(typewriterTimerRef.current);
        typewriterTimerRef.current = null;
      }
      setIsTyping(false);
      if (onComplete) {
        onComplete();
      }
    }
  }, [isFinished, data, onComplete]);

  // 组件卸载时清理定时器
  useEffect(() => {
    return () => {
      if (typewriterTimerRef.current) {
        clearInterval(typewriterTimerRef.current);
      }
    };
  }, []);

  // 逐字打字机效果
  const startTypewriter = () => {
    if (typewriterTimerRef.current) {
      clearInterval(typewriterTimerRef.current);
    }

    setIsTyping(true);

    typewriterTimerRef.current = setInterval(() => {
      setTextQueue(prevQueue => {
        if (prevQueue.length === 0) {
          setIsTyping(false);
          clearInterval(typewriterTimerRef.current);
          typewriterTimerRef.current = null;
          return '';
        }

        const nextChar = prevQueue[0];
        setDisplayText(prevText => prevText + nextChar);
        return prevQueue.slice(1);
      });
    }, 20); // 20ms间隔，更快更流畅
  };

  // 添加文本到队列
  const addTextToQueue = (text) => {
    console.log('Text组件添加文本到队列:', text); // 调试日志
    setTextQueue(prevQueue => {
      const newQueue = prevQueue + text;
      console.log('Text组件新队列:', newQueue); // 调试日志
      return newQueue;
    });

    // 如果当前没有在打字，开始打字机效果
    if (!typewriterTimerRef.current) {
      console.log('Text组件开始打字机效果'); // 调试日志
      startTypewriter();
    }
  };

  return (
    <div className={`text-container ${isFinished ? 'finished' : ''}`}>
      <div className="text-output">
        {displayText}
        {isTyping && <span className="cursor">|</span>}
      </div>
    </div>
  );
};

export default Text;
